class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        set<int> sett;
        for (int i = 0; i < n; ++i) {
            sett.insert(i);
        }

        vector<int> ans;

        for (const auto& qi : q) {
            int left = qi[0];
            int right = qi[1];

            auto lowerb = sett.lower_bound(left + 1);
            auto upperb = sett.lower_bound(right);

            sett.erase(lowerb, upperb);

            ans.push_back(sett.size() - 1);
        }

        return ans;
    }
};
