class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int size = queries.size();
        vector<int> ans(size);
        vector<int> s;
        for(int i=0;i<n;i++){
            s.push_back(i);
        }
        int j = 0;
        for(int i=0;i<size;i++){
            int x = queries[i][0], y = queries[i][1];
            auto lower = lower_bound(s.begin(),s.end(),x+1);
            auto upper = lower_bound(s.begin(),s.end(),y);
            s.erase(lower,upper);
            ans[j] = s.size()-1;
            j++;
        }
        return ans;
    }
};
