class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> ans;
        int curr=n-1;
        int q=queries.size();
        set<pair<int,int>> v;
        for(int i=0;i<q;i++)
        {
            int st=queries[i][0];
            int en=queries[i][1];
            auto it=v.lower_bound(make_pair(st,-en));
            while(it!=v.end())
            {
                it=v.lower_bound(make_pair(st,-en));
                if(it!=v.end()&&it->first<en)
                {
                    cout<<":"<<it->first<<" "<<it->second<<endl;
                    curr+=(-(it->second)-(it->first)-1);
                    v.erase(it); 
                }
                else
                {
                    break;
                }
            }
            int flag=1;
            it=v.lower_bound(make_pair(st,-en));
            if(it!=v.begin())
            {
                it--;
                if(it->first<=st&&(-it->second)>=en)flag=0;
            }
            //cout<<curr<<" ";
            if(flag)
            {
                curr-=(en-st-1);
                v.insert(make_pair(st,-en));
            }
            // cout<<curr<<endl;
            // cout<<"(";
            // for(auto x:v)
            // {
            //     cout<<x.first<<","<<x.second<<"  ";
            // }
            // cout<<endl;
            ans.push_back(curr);
        }
        return ans;
    }
};
