class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        set<int>s;
        int ans = INT_MAX;
        vector<int>res;
        vector<int>vec;

        int mini = -1, maxi = -1;
         vector<int> distance(n, 0);
        for (int i = 1; i < n; ++i) {
            distance[i] = distance[i - 1] + 1;
        }

        set<int> p_set;
        for (int i = 0; i < n; ++i) {
            p_set.insert(i);
        }


        for (const auto& qi : q) {
            int l = qi[0];
            int rt = qi[1];

            auto lower = p_set.lower_bound(l + 1);
            auto upper = p_set.lower_bound(rt);

            p_set.erase(lower, upper);

            res.push_back(p_set.size() - 1);
        }

        return res;

    }
};
