class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {

        map<int,int> Map ;
        int shrink = 0 ;

        vector<int> ans ;

        for( auto q : queries ){

            int a = q[0] ;
            int b = q[1] ;
            int currShrink = b-a-1 ;

            // for( auto m : Map ) cout << m.first << "-" << m.second << endl ;

            auto it = Map.lower_bound(a) ;
            vector<int> toRemove ;
            if( it != Map.end() ){

                for( auto i=it ; it != Map.end() ; it++ ){
                    if( a <= it->first  && it->second <= b ){
                        currShrink -= (it->second - it->first -1) ;
                        toRemove.push_back(it->first) ;
                    }
                    else{
                        break ;
                    }
                }
            }

            // for( auto j : toRemove ) cout << j << " " ;
            // cout << endl ;
            for( auto j : toRemove ) Map.erase(j) ;

            auto upper = Map.upper_bound(a);
            if (upper != Map.begin()) {
                auto p = prev(upper);
                // cout << p->first << " " << p->second << endl ;
                if( p->first <= a && b <= p->second){
                    // cout <<"not taken" << endl ;
                    currShrink = 0 ;
                }
                else{
                    Map[a] = b ;
                }
            }
            else{
                Map[a] = b ;   
            }            

            shrink += currShrink ;
            ans.push_back(n-1-shrink) ;

            // cout << a << "-" << b << " " << currShrink << " " << shrink << endl ;
        }

        // cout << endl ;

        return ans ;

    }
};
