class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        vector<int> ans;
        for(int i = 0; i < n; i++)
            s.insert(i);
        for(auto &val:queries){
            int l = val[0], r = val[1];
            while(true){
                auto it = s.upper_bound(l);
                if(it == s.end() || *it >= r)
                    break;
                s.erase(it);
            }
            ans.push_back(s.size()-1);
        }
        return ans;
    }
};
