#include <vector>
#include <set>

class Solution {
public:
    set<int> st;
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        // vector<int> dis(n, 0),ans;
        // for (int i=1;i<n;i++) dis[i]=dis[i-1]+1;
        vector<int> ans;
        for (int i=0;i<n;i++) st.insert(i);

        for (int i=0;i<q.size();i++) {
            int x = q[i][0],y=q[i][1];  auto itl = st.lower_bound(x + 1); auto itr = st.lower_bound(y);
            st.erase(itl, itr);
            int tans=st.size(); tans--;
            ans.push_back(tans);
        }
        return ans;
    }
};
