class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        unordered_map<int,int> m;
        for(int i =0;i<(n-1);i++)
        {
            m[i] = i+1;
        }
        vector<int> tor;
        int len = n-1;
        for(auto i : queries)
        {
            int start = i[0];
            int end = i[1];
            int removed =0;
            while(m.find(start)!=m.end()&&start<end)
            {
                int temp = start;
                start = m[temp];
                m.erase(temp);
                removed++;
            }
            m[i[0]] = i[1];
            if(removed>0)
            {
                len = len-removed+1;
            }
            tor.push_back(len);
        }
        return tor;
    }
};
