class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int len = n - 1;
        vector<int> adj(n);
        for (int i = 0; i < n; i++) {
            adj[i] = i + 1;
        }

        vector<int> res;
        for (auto &q : queries) {
            int u = q[0];
            int v = q[1];
            if (adj[u] == -1 || adj[v] == -1) {
                res.push_back(len);
                continue;
            }

            int i = adj[u];
            while (i != v) {
                int next = adj[i];
                adj[i] = -1;
                i = next;
                len--;
            }
            adj[u] = v;
            res.push_back(len);
        }

        return res;
    }
};
