class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        for (int i=0;i<n;++i)  s.insert(i);
        vector<int> res;
        for (auto& q : queries) {
            int l = q[0],r = q[1];
            auto u = s.lower_bound(l + 1);
            auto v = s.lower_bound(r);
            s.erase(u, v);
            int m=s.size();
            res.push_back(m-1);
        }
        return res;
    }
};
