class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> dist(n, 0);
        for (int i = 1; i < n; i++) {
            dist[i] = dist[i - 1] + 1;
        }
        set<int> st;
        for (int i = 0; i < n; i++) {
            st.insert(i);
        }
        vector<int> ans;
        for (auto& query : queries) {
            auto lb = st.lower_bound(query[0] + 1);
            auto ub = st.lower_bound(query[1]);
            st.erase(lb, ub);
            ans.push_back(st.size() - 1);
        }
        return ans;
    }
};
