class Solution {
public:
    int adjust(set<int> &st,int u,int v,vector<int> &sht)
    {
        int ans=0;
        vector<int> temp;
        auto it = st.lower_bound(u);
        while(it!=st.lower_bound(v))
        {
            temp.push_back(*it);
            it++;
        }
        for(auto x:temp)
        {
            ans+=sht[x];
            st.erase(x);
        }
        sht[u]=v-u-1;
        st.insert(u);
        return ans;
    }
    bool chk(set<int> &st,int u,int v)
    {
        if(v-u<=1)
            return 0;

        if(st.find(u)==st.end() || st.find(v)==st.end())
            return 0;

        vector<int> temp;
        auto it = st.lower_bound(u+1);
        while(it!=st.lower_bound(v))
        {
            temp.push_back(*it);
            it++;
        }
        for(auto x:temp)
        {
            st.erase(x);
        }

        return 1;
    }
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) 
    {
        vector<int> ret;
        int ans = n-1;
        vector<int> sh(n,0);
        set<int> st;
        set<int> done;
        for(int i=1;i<=n;i++)
            done.insert(i-1);
        for(auto x : queries)
        {
            int u = x[0];
            int v = x[1];

            if(chk(done,u,v))
            {
                int sht = adjust(st,u,v,sh);
                ans-=(v-u-1-sht);
            }
            ret.push_back(ans);
        }
        return ret;
    }
};
