class Solution {
public:
    void make(int i,int j,vector<int> adj[]){
        while(i<=j){
            adj[i][0]=-1;
            i++;
        }
    }

    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& qr) {
        int curSum=n-1 ;
        set<int> st ;
        for(int i=0;i<n;i++) st.insert(i) ;
        int q = qr.size() ;
        vector<int> ans(q) ;



        for(int i=0;i<q;i++){
           int u=qr[i][0], v=qr[i][1] ;
           auto start = st.upper_bound(u) ;
           auto end = st.lower_bound(v) ;

           if(st.find(u)!=st.end() && st.find(v)!=st.end()){
                auto start = st.upper_bound(u) ;
                auto end = st.lower_bound(v) ;
                st.erase(start,end) ;
                st.insert(v) ;
           }

           else if(st.find(v)!=st.end()){
                auto start = st.upper_bound(u) ;
                auto end = st.lower_bound(v) ;
                st.erase(start,end) ;
                st.insert(v) ;
           }else if(st.find(u)!=st.end()){
                auto start = st.upper_bound(u) ;
                auto end = st.lower_bound(v) ;
                st.erase(start,end) ;
                st.insert(*end) ;
           }
           else{
                auto start = st.upper_bound(u) ;
                auto end = st.lower_bound(v) ;
                st.erase(start,end) ;
                st.insert(*end) ;
           }

           int res = st.size() ;
           ans[i] = res-1 ;
        }

        return ans ;
    }
};
