#define ll long long int
#define un unsigned long long
#define all(v) v.begin(),v.end()
#define pb push_back
#define sz(x) ((int)(x).size())

class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        vector<vector<int>> graph(n);
    for (int i = 0; i + 1 < n; ++i)
    {
        graph[i].pb(i + 1);
        // graph[i + 1].pb(i);
    }
    vector<int> ans;
    set<int>dif;
    for (int i = 0; i < n; ++i) dif.insert(i);
    for (auto j : q) {
        int x = j[0], y = j[1];
        auto it = dif.lower_bound(x + 1);
        auto end = dif.lower_bound(y);
        dif.erase(it, end);

        ans.push_back(dif.size() - 1);
    }
    return ans;
    }
};
