class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n,
                                             vector<vector<int>>& queries) {
        vector<int> ans;
        set<int> st;
        for (int i = 0; i < n; i++)
            st.insert(i);
        for (auto& q : queries) {
            auto lb = st.lower_bound(q[0] + 1);
            auto ub = st.lower_bound(q[1]);
            st.erase(lb, ub);
            ans.push_back(st.size() - 1);
        }
        return ans;
    }
};
