class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> ans;
        vector<int> g(n + 1);
        int u, v, dis = n - 1;

        for(int i = 0; i < n - 1; i++){
            g[i] = i + 1;
        }
        for(auto q:queries){
            u = q[0], v = q[1];
            if(v > g[u]){
                int cnt = 0;
                while(u < v){
                    cnt++;
                    int temp = g[u];
                    g[u] = n;
                    u = temp;
                }
                dis = dis - (cnt - 1);
                g[q[0]] = v;
            }
            ans.push_back(dis);
        }
        return ans;
    }
};
