class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> distances(n, 0);
        for (int i = 1; i < n; ++i) {
            distances[i] = distances[i - 1] + 1;
        }

        set<int> positions;
        for (int i = 0; i < n; ++i) {
            positions.insert(i);
        }

        vector<int> results;

        for (const auto& query : queries) {
            int start = query[0];
            int end = query[1];

            auto lowerBound = positions.lower_bound(start + 1);
            auto upperBound = positions.lower_bound(end);

            positions.erase(lowerBound, upperBound);

            results.push_back(positions.size() - 1);
        }

        return results;
    }
};
