class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {

        set<int> positions;
        for (int i = 0; i < n; ++i) {
            positions.insert(i);
        }

        vector<int> result;
        for (const auto& query : queries) {
            int left = query[0];
            int right = query[1];

            removePositionsInRange(positions, left, right);


            result.push_back(positions.size() - 1);
        }

        return result;
    }

private:
    void removePositionsInRange(set<int>& positions, int left, int right) {

        auto startIt = positions.lower_bound(left + 1);
        auto endIt = positions.lower_bound(right);

        positions.erase(startIt, endIt);
    }
};
