class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        vector<int> vis(n,0),ans;
        set<int> path;
        for(int i=0;i<n;i++){
            path.insert(i);
            vis[i]=1;
        }
        for(int i=0;i<q.size();i++){
            int u=q[i][0];
            int v=q[i][1];
            if(vis[u]&&vis[v]){
                int d=*path.upper_bound(u);
                while(d!=v){
                    vis[d]=0;
                    int tmp=d;
                    d=*path.upper_bound(d);
                    path.erase(tmp);
                }
            }
            ans.push_back(path.size()-1);
        }
        return ans;
    }
};

