class Solution {
public:
    std::vector<int> shortestDistanceAfterQueries(int n, std::vector<std::vector<int>>& queries) {
        std::set<int> orz;
        for (int i = 0; i < n; ++i) {
            orz.insert(i);
        }
        std::vector<int> ans;
        for (const auto& q : queries) {
            int start = q[0] + 1;
            int end = q[1];
            auto it = orz.lower_bound(start);
            while (it != orz.end() && *it < end) {
                it = orz.erase(it);
            }
            ans.push_back(orz.size() - 1);
        }
        return ans;
    }
};
