class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        set<int> st;
        for (int i = 0; i < n; ++i) {
            st.insert(i);
        }
        vector<int> dp(n, 0);
        for (int i = 1; i < n; ++i) {
            dp[i] = dp[i - 1] + 1;
        }
        vector<int> res;
        for (const auto& i : q) {
            int lft = i[0];
            int rt = i[1];
            auto lb = st.lower_bound(lft + 1);
            auto ub = st.lower_bound(rt);
            st.erase(lb, ub);
            res.push_back(st.size() - 1);
        }
        return res;
    }
};
