class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        set<int> s;
        for (int i = 0; i < n; i++) s.insert(i);
        int cur = n - 1;
        vector<int> res;
        for (int i = 0; i < q.size(); i++) {
            int x = q[i][0], y = q[i][1];
            auto it = s.lower_bound(x);
            if (it == s.end() || *it != x) {
                res.push_back(cur);
                continue;
            }
            auto it2 = s.lower_bound(y);
            if (it2 == s.end() || *it2 != y) {
                res.push_back(cur);
                continue;
            }
            it++;
            while (it != s.end() && *it != y) {
                it = s.erase(it);
            }
            cur = s.size() - 1;
            res.push_back(cur);
        }
        return res;
    }
};
