class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int m = queries.size();
        vector<int> ans(m, n - 1);
        vector<int> next_pos(n, -1);
        for(int i = 0; i < n; i ++ ) {
            next_pos[i] = i + 1;
        }
        int res = n - 1;
        vector<int> dp(n, 1);
        for(int i = 0; i < m; i ++ ) {
            int u = queries[i][0];
            int v = queries[i][1];
            if(v <= next_pos[u]) {
                ans[i] = res;
                continue;
            }
            int pv = next_pos[u];
            bool flag = false;
            for(int j = u; j < v; j = next_pos[j] ) {
                if(dp[j] == 1) {
                    flag = true;
                }
                else{
                    break;
                }
                res -= dp[j];
                dp[j] = 0;
            }
            next_pos[u] = v;
            if(flag) {
                dp[u] = 1;
                res += 1;
            }
            ans[i] = res;
        }
        return ans;
    }
};
