class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> st;
        for (int i = 0; i < n; ++i)
            st.insert(i);
        int q = queries.size(), sum = n - 1;
        vector<int> ans(q);
        for (int i = 0; i < q; ++i) {
            int u = queries[i][0], v = queries[i][1];
            if (st.count(u) && st.count(v)) {
                auto it = st.lower_bound(u);
                ++it;
                while (it != st.end() && (*it) < v)
                    it = st.erase(it), --sum;
            }
            ans[i] = sum;
        }
        return ans;
    }
};
