class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int>dist(n, 0);
        for (int i=1;i<n;i++) {
            dist[i]=dist[i-1]+1;
        }

        set<int>st;
        for (int i = 0; i < n; i++) {
            st.insert(i);
        }

        vector<int> ans;
        for (auto it : queries) {
            int u =it[0];
            int v =it[1];

            auto lowerBound =st.lower_bound(u + 1);
            auto upperBound =st.lower_bound(v);

            st.erase(lowerBound, upperBound);

            ans.push_back(st.size() - 1);
        }

        return ans;
    }
};
