class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int m = queries.size();
        set<int> s;
        vector<int> ans;
        vector<int> next(n);
        for (int i = 0; i < n - 1; i++) {
            next[i] = i + 1;
            s.insert(i);
        }
        s.insert(n-1);
        for (int i = 0; i < m; i++) {
            int low = queries[i][0];
            int high = queries[i][1];
            for (int i = next[low]; i < high; i++) {
                s.erase(i);
                if(next[i]!=i+1){
                    i = next[i]-1;
                }
                if(next[i]<high)
                    next[i] = high;
            }
            // for(auto set:s){
            //     cout<<set<<" ";
            // }
            // cout<<endl;
            if(next[low]<high)
                next[low] = high;
            ans.push_back(s.size()-1);
        }
        return ans;
    }
};
