class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int cnt = 0;
        vector<int> ans;
        map<int,int> mp ;
        for(auto a : queries)
        {

                auto x = mp.upper_bound(a[0]);
                int f = 1;
                if( (x!=mp.begin()) && (prev(x)->first != a[0]) &&  (prev(x)->second >a[0]))
                {
                    f = 0;
                }
                else if(mp.count(a[0])  &&  (mp[a[0]] < a[1]))
                {

                    x = mp.upper_bound(a[0]);
                    for(;x!=mp.end() && (x->first < a[1]) && (x->first > a[0] );x++)
                    {
                        cnt-=(x->second-x->first-1);
                        mp.erase(x);
                    }
                    cnt-=(mp[a[0]]-a[0]-1);
                    cnt+=(a[1]-a[0]-1);
                    mp[a[0]] = a[1];
                }
                else if(mp.count(a[0]) == 0 )
                {
                    x = mp.upper_bound(a[0]);
                    for(;x!=mp.end() && (x->first < a[1]) && (x->first > a[0] );x++)
                    {
                        cnt-=(x->second-x->first-1);
                        mp.erase(x);
                    }
                    cnt+=(a[1]-a[0]-1);
                    mp[a[0]] = a[1];
                }


                ans.push_back(n-1-cnt);

        }
        return ans;
    }
};
