class Solution {
    public int[] shortestDistanceAfterQueries(int n, int[][] queries) {
        TreeMap<Integer,Integer> map = new TreeMap();
        for(int i=0;i<n-1;i++){
            map.put(i,i+1);
        }
        int m = queries.length;
        int[] ans = new int[m];
        for(int i=0;i<m;i++){
            int[] q = queries[i];
            int s = q[0], e = q[1];
            Integer x = map.floorKey(s), y = map.ceilingKey(s);
            if(x!=null && map.get(x)>=e){
                ans[i] = map.size();
                continue;
            }
            while(y!=null && map.get(y)<=e){
                map.remove(y);
                y = map.ceilingKey(y+1);
            }
            map.put(s,e);
            ans[i] = map.size();
        }
        return ans;
    }
}
