class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {

        vector<int> res;
        int dist = n-1;
        set<pair<int, int>> I;

        for(int i=0; i<n-1; ++i)
            I.insert({i, i+1});

        for(auto& q : queries) {

            auto it = I.lower_bound({q[0], 0});

            if((it != begin(I) && prev(it)->second >= q[1]) || (it->first == q[0] && it->second >= q[1])) {
                res.push_back(dist);
                continue;
            }



            while(it != end(I) && it->second <= q[1]) {
                I.erase(it++);
                dist--;
            }

            I.insert({q[0], q[1]});
            res.push_back(++dist);


        }

        return res;

    }
};
