from sortedcontainers import SortedDict

class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        dis = n - 1
        d = SortedDict({-1: -1})

        ans = []

        for a, b in queries:
            idx = d.bisect_right(a) - 1
            if d.values()[idx] >= b:
                ans.append(dis)
                continue

            if d.keys()[idx] != a:
                idx += 1

            while idx < len(d) and d.values()[idx] <= b:
                l, r = d.popitem(idx)
                dis += r - l - 1

            d[a] = b
            dis -= b - a - 1
            ans.append(dis)

        return ans
