class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        vector<int> dist(n, 0);
        for (int i = 1; i < n; ++i) {
            dist[i] = dist[i - 1] + 1;
        }

        set<int> p;
        for (int i = 0; i < n; ++i) {
            p.insert(i);
        }

        vector<int> ri;

        for ( auto qi : q) {
            int l = qi[0];
            int rt = qi[1];

            auto lb = p.lower_bound(l + 1);
            auto ub = p.lower_bound(rt);

            p.erase(lb, ub);

            ri.push_back(p.size() - 1);
        }

        return ri;
    }
};
