class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        int m = q.size();
        vector<int> par(n), ans(m);
        iota(par.begin(), par.end(), 0);
        function<int(int)> f = [&](int a) {
            return par[a] == a ? a : par[a] = f(par[a]);
        };
        for (int i = 0; i < m; i++) {
            ans[i] = i ? ans[i - 1] : n - 1;
            int ta = f(q[i][0]), tb = f(q[i][1]);
            if (ta == tb)
                continue;
            while (tb != q[i][0] + 1) {
                ans[i]--;
                int tc = f(tb - 1);
                par[tb] = tc;
                tb = tc;
            }
        }
        return ans;
    }
};
