class Solution {
public:
    vector<int>shortestDistanceAfterQueries(int _num_nodes_, vector<vector<int>>& _queries_) {
        vector<int> _distances_(_num_nodes_, 0);
        for (int _i_ = 1; _i_ < _num_nodes_; ++_i_) {
            _distances_[_i_] = _distances_[_i_ - 1] + 1;
        }

        set<int> _positions_;
        for (int _i_ = 0; _i_ < _num_nodes_; ++_i_) {
            _positions_.insert(_i_);
        }

        vector<int> _results_;

        for (const auto& _query_ : _queries_) {
            int _left_bound_ = _query_[0];
            int _right_bound_ = _query_[1];

            auto _lower_bound_ = _positions_.lower_bound(_left_bound_ + 1);
            auto _upper_bound_ = _positions_.lower_bound(_right_bound_);

            _positions_.erase(_lower_bound_, _upper_bound_);

            _results_.push_back(_positions_.size() - 1);
        }

        return _results_;
    }
};
