class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> pos(n);
        vector<int> res;
        for(int i = 0;i < n;i++){
            pos[i] = i;
        }
        for(auto q:queries){
            auto it1 = lower_bound(pos.begin(),pos.end(),q[0]);
            auto it2 = lower_bound(pos.begin(),pos.end(),q[1]);
            if(*it1 == q[0] && *it2 == q[1]){
                it1++;
                pos.erase(it1,it2);
            }
            res.emplace_back(pos.size()-1);
        }
        return res;
    }
};
