public class Solution {
    public int[] ShortestDistanceAfterQueries(int n, int[][] queries) {
        int[] next=new int[n];
        int[] ans = new int[queries.Length];
        int len = n-1;
        for(int i=0;i<n;i++) 
        {
            next[i] = i+1;
        }
        for(int k=0;k<queries.Length;k++)
        {
            int f = queries[k][0];
            int t = queries[k][1];
            if(next[f]>0 && next[f]< t)
            {
                int step = 0;
                while(f!=t)
                {
                    int tmp=next[f];
                    next[f]=-1;
                    f = tmp;
                    step++;
                }
                len -= step-1;
                next[queries[k][0]] = t;
            }
            ans[k]=len;
        }
        return ans;

    }
}
