class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> ans;
        vector<int> nxt(n, -1);
        for(int i=0; i<n-1; i++) {
            nxt[i] = i+1;
        }

        int sum = n-1;
        for(auto& it: queries) {
            int l = it[0], r = it[1];
            if (nxt[l] == -1) {
                ans.push_back(sum);
                continue;
            }

            int i = l;
            while (i < r) {
                int temp = nxt[i];
                sum--;
                nxt[i] = -1;
                i = temp;
            }
            nxt[l] = r;

            sum++;
            ans.push_back(sum);
        }

        return ans;
    }
};
