class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int removed =0;
        map<int,int> mp;
        vector<int> res;
        int remove_num =0;
        for(auto const &query:queries){
            int from =query[0];
            int to =query[1];
            auto iter =mp.lower_bound(from);
            auto iter1 =mp.begin();
            if(iter !=mp.begin()){
                iter1 =--iter;
                if(iter1->first<=from && iter1->second>=to){
                    res.push_back(n-remove_num-1);
                    continue;
                }
                iter++;
            }
            while(iter!=mp.end() && iter->first>=from && iter->first<to && iter->second<=to)
            {
                remove_num -= iter->second-iter->first-1;
                iter=mp.erase(iter);
            }
            if(!mp.count(from))
            {
                mp[from]=to;
                //cout<<to<<','<<from<<","<<remove_num<<";";
                remove_num += to-from-1;
            }
            res.push_back(n-remove_num-1);
           // for(auto [a,b]:mp){
                //cout<<a<<','<<b<<","<<remove_num<<";";
           // }
            //cout<<"\n";    
        }
        return res;
    }
};
