class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int dp = n - 1;
        map<int, int> record;
        vector<int> res;
        for(int i = 0; i < n; i++)
        {
            record[i] = i + 1;
        }
        for(int m = 0; m < queries.size(); m++)
        {
            auto start = record.lower_bound(queries[m][0]);
            if(start->first == queries[m][0]) 
            {
                if(start->second >= queries[m][1]) {res.push_back(dp); continue;}
                else
                {
                    int num = 0;
                    while(start != record.end() && start->first < queries[m][1])
                    {
                        num++;
                        auto now = start;
                        start++;
                        record.erase(now);
                    }
                    dp -= num - 1;
                    record[queries[m][0]] = queries[m][1];
                    res.push_back(dp);
                    continue;
                }
            }
            else
            {
                if(start != record.begin())
                {
                    start--;
                    if(start->second >= queries[m][1]) {res.push_back(dp);continue;}
                    start++;
                }
                int num = 0;
                while(start != record.end() && start->first < queries[m][1])
                {
                    num++;
                    auto now = start;
                    start++;
                    record.erase(now);
                }
                dp -= num - 1;
                record[queries[m][0]] = queries[m][1];
                res.push_back(dp);
                continue;
            }
        }

        return res;
    }
};
