class Solution {
public:
    class node{
        public:
            int val = -1;
            node*next = NULL;
            node(int val){
                this->val = val;
            }
            node(int val,node*next){
                this->val = val;
                this->next = next;
            }
    };

    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries){
        vector<int> v(n);
        vector<bool> Index(n,true);

        for(int i=n-1;i>=0 ;i--) {
            v[i] = i+1;
        }

        int size = n-1;
        vector<int> ans;

        for(vector<int> query:queries){
            if(!Index[query[0]]){
                ans.push_back(size);
            }
            else{
                if( v[query[0]] < query[1]){
                    for(int i= v[query[0]] ;i<query[1];){
                        Index[i] = false;
                        size --;
                        i = v[i];
                    }
                    v[query[0]] = query[1];
                }
                ans.push_back(size);
            }
        }    
        return ans;
    }
};
