class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        for(int i=0;i<n;i++){
            s.insert(i);
        }
        vector<int> ans;
        for(auto it:queries ){
            int u = it[0],v = it[1];
            while(s.size()){
                auto k = s.upper_bound(u);
                if(*k>=v)break;
                else s.erase(k);
            }
            ans.push_back(s.size()-1);
        }
        return ans;
    }
};
