class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<pair<int, int>> intervals;
        vector<int> ans;
        for(int i = 0; i < n - 1; i++) intervals.insert({i, i + 1});

        for(auto it: queries) {
            // cout << "Set is: ";
            // for(auto i: intervals) cout << i.first << ":" << i.second << ", ";
            // cout << endl;

            int a = it[0], b = it[1];
            if(a > b) swap(a, b);
            // cout << a << " " << b << endl;

            auto ind = intervals.lower_bound({a, INT_MIN});
            pair<int, int> val = *ind;
            // cout << "Val: " << val.first << " " << val.second << endl;
            if(ind == intervals.end() || val.first != a || val.second > b) {
                ans.emplace_back(intervals.size());
                continue;
            }

            auto end = intervals.lower_bound({b, INT_MIN});
            // if(end != intervals.end()) cout << "end: " << (*end).first << " " << (*end).second << endl;
            // else cout << "ENDED" << endl;

            // unordered_
            intervals.erase(ind, end);
            intervals.insert({a, b});

            ans.push_back(intervals.size());
        }

        return ans;
    }
};
