#include <vector>
#include <set>
#include <iostream>

using namespace std;

class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int nodes, vector<vector<int>>& queries) {
        vector<int> distances(nodes, 0);


        for (int i = 1; i < nodes; ++i) {
            distances[i] = distances[i - 1] + 1;

        }

        set<int> availablePositions;


        for (int i = 0; i < nodes; ++i) {
            availablePositions.insert(i);
            // cout << "Position " << i << " added to the set." << endl;
        }

        vector<int> results;


        for (const auto& query : queries) {
            int startPos = query[0];
            int endPos = query[1];


            auto lowerBoundIt = availablePositions.lower_bound(startPos + 1);


            auto upperBoundIt = availablePositions.lower_bound(endPos);


            availablePositions.erase(lowerBoundIt, upperBoundIt);


            results.push_back(availablePositions.size() - 1);

        }

        // cout << "Returning final results vector." << endl;
        return results;
    }
};
