class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        d = {i:i + 1 for i in range(n -1 )}
        # print(d)
        res = []
        for i, j in queries:
            if i in d and d[i] < j:
                cur, nxt = i, d[i]
                while nxt <= j:
                    # print("delete" , i, j, cur)
                    del d[cur]
                    cur = nxt
                    if cur not in d:
                        break
                    nxt = d[cur]
                d[i] = j

            # print(d)
            res.append(len(d))
        return res
