class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int>xx;
        for(int i=0;i<n;i++){
            xx.insert(i);
        }
        vector<int>ans;
        for(auto vv:queries){
            while(1){
                if(xx.upper_bound(vv[0])!=xx.end()){
                    int p=*xx.upper_bound(vv[0]);
                    if(p<vv[1]){
                        xx.erase(p);
                    }else break;

                }else break;

            }
            ans.push_back(xx.size()-1);
        }
        return ans;
    }
};
