class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int sz = n-1;
        vector<int> a;
        map<int,ListNode*> m;
        ListNode *head = new ListNode(0);
        m[0]=head;
        auto p = head;
        vector<bool> del(n,0);
        for(int i=1;i<n;++i) {
            p -> next = new ListNode(i);
            p = p->next;
            m[i] = p;
        }
        for(auto &t:queries) {
            if(del[t[0]]||del[t[1]]) {


            a.push_back(sz);
                continue;
            }
            ListNode* q = m[t[0]];
            while(q->next && q->next->val <t[1]) {
                del[q->next->val]=1;
                sz--;
                q ->next = q->next->next;
            }

            a.push_back(sz);
        }
        return a;
    }
};
