class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> dye(n), zero(n);
        for(int i = 0; i < n; i++) zero[i] = i + 1;
        vector<int> ans;
        int cnt = n;
        for(auto &q : queries) {
            int i = q[0], j = q[1];
            if(dye[i] == true || dye[j] == true) {
                ans.emplace_back(cnt - 1);
            } else {
                for(int k = zero[i]; k < j; k = zero[k]) {
                    if(dye[k] == false) {
                        cnt--;
                        dye[k] = true;
                    }
                    else break;
                }
                zero[i] = j;
                ans.emplace_back(cnt - 1);
            }
        }
        return ans;
    }
};
