class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        vector<int> dp(n, 0);
        for (int i = 1; i < n; ++i) {
            dp[i] = dp[i - 1] + 1;
        }

        set<int> m;
        for (int i = 0; i < n; ++i) {
            m.insert(i);
        }

        vector<int> r;

        for (const auto& qi : q) {
            int l = qi[0];
            int rt = qi[1];

            auto lowerbound = m.lower_bound(l + 1);
            auto upperbound = m.lower_bound(rt);

            m.erase(lowerbound, upperbound);

            r.push_back(m.size() - 1);
        }

        return r;
    }
};
