class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        for(int i=0;i<n;i++)
            s.insert(i);
        vector<int> ans;
        for(auto q:queries) {
            queue<int> qu;
            for(auto it = s.lower_bound(q[0]+1);it!=s.end() && (*it)<q[1];it++) {
                qu.push(*it);
            }
            while(qu.size()) {
                s.erase(qu.front());
                qu.pop();
            }
            ans.push_back((int)s.size()-1);
        }
        return ans;
    }
};
