class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> graph(n, 0);
        for (int i = 1; i < n; i++)
            graph[i - 1] = i;
        int cnt = 0;

        vector<int> result;

        for (vector<int> &querie: queries) {
            int a = querie[0];
            int b = querie[1];

            if (graph[a] != -1 && b > graph[a]) {
                int _cnt = 0;
                int x = a;

                while (x != b) {
                    int tmp = x;
                    _cnt++;
                    x = graph[x];
                    if (tmp != a)
                        graph[tmp] = -1;
                }
                cnt += _cnt - 1;
                graph[a] = b;
            }

            result.push_back(n - 1 - cnt);
        }

        return result;
    }
};
