func shortestDistanceAfterQueries(n int, queries [][]int) []int {
    rss := make([]int, len(queries))
    nextArr := make([]int, n)
    prevArr := make([]int, n+1)
    for i := 0; i<n; i++ {
        nextArr[i] = i+1
        prevArr[i+1] = i
    }
    var rs = n - 1
    doneMap := make([]bool, n+1)
    for idx, req := range queries {
        // 1 6 2 5
        if doneMap[req[0]] {
            rss[idx] = rs
            continue
        }
        if nextArr[req[0]] < req[1] && prevArr[req[1]] > req[0] {
            var cnt int
            cur := nextArr[req[0]]
            for cur < req[1] {
                cnt++
                //fmt.Println("cur", cur, cnt)
                doneMap[cur] = true
                cur = nextArr[cur]
            }
            nextArr[req[0]] = req[1]
            prevArr[req[1]] = req[0]
            rs = rs - cnt
        } 
        rss[idx] = rs
    }
    return rss
}
